import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';


function MovieList(props) {

    let myMovies = props.movies;
    //let delMovie = props.deleteMovie;


    return (
        <div className="movieBlockContainer">
            {myMovies.map((movie) => (
                <div key={movie.id} className="movieBlock">
                    <h1>{movie.title}</h1>
                    <img src={movie.image}/>
                    <h5>Released: {movie.date}</h5>
                    <p>{movie.actors}</p>
                    <p>{() => {
                        let number = movie.rating; // or any number between 1-5
                        let stars = "";

                        switch (number) {
                            case 1:
                                stars = "⭐";
                                break;
                            case 2:
                                stars = "⭐⭐";
                                break;
                            case 3:
                                stars = "⭐⭐⭐";
                                break;
                            case 4:
                                stars = "⭐⭐⭐⭐";
                                break;
                            case 5:
                                stars = "⭐⭐⭐⭐⭐";
                                break;
                            default:
                                // handle invalid input
                                stars = "Invalid Rating"
                                break;
                        }
                        return stars;
                    }}</p>

                    <Button variant="primary" onClick={() => {
                        props.deleteMovie(movie.id);
                    }}>Delete</Button>
                </div>
            ))}
        </div>
    );
}

export default MovieList;
