import React, { useState, useEffect } from 'react';

let testMovie =   {
    "id": 66,
    "title": "The Hooters",
    "date": "1994-09-23",
    "actors": "Tim Robbins, Morgan Freeman, Bob Gunton, William Sadler, Clancy Brown",
    "image": "https://via.placeholder.com/400x200",
    "rating": "⭐⭐⭐⭐⭐"
}

function AddMovie(props){

    const [title, setTitle] = useState('');
    const [date, setDate] = useState('');
    const [actors, setActors] = useState('');
    const [image, setImage] = useState('');
    const [rating, setRating] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        // Do something with the form data
    };


    return(
        <div>
            <h2>Add A Movie Rating</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="title">Title</label>
                <input type="text" id="title" value={title} onChange={(event) => setTitle(event.target.value)} />

                <label htmlFor="date">Date</label>
                <input type="date" id="date" value={date} onChange={(event) => setDate(event.target.value)} />

                <label htmlFor="actors">Actors</label>
                <input type="text" id="actors" value={actors} onChange={(event) => setActors(event.target.value)} />

                <label htmlFor="image">Image</label>
                <input type="url" id="image" value={image} onChange={(event) => setImage(event.target.value)} />

                <label htmlFor="rating">Rating</label>
                <input type="number" id="rating" value={rating} onChange={(event) => setRating(event.target.value)} />

                <button type="submit">Submit</button>
            </form>
            <button onClick={() => {
                    props.movies.push(testMovie);
                }
            }>ADD REVIEW</button>
        </div>

    );
}

export default AddMovie;
