import React, { useState, useEffect } from 'react';

let movieData = [
    {
        "title": "The Shawshank Redemption",
        "date": "1994-09-23",
        "actors": "Tim Robbins, Morgan Freeman, Bob Gunton, William Sadler, Clancy Brown",
        "image": "https://via.placeholder.com/400x200",
        "rating": "⭐⭐⭐⭐⭐"
    },
    {
        "title": "The Godfather",
        "date": "1972-03-24",
        "actors": "Al Pacino, Marlon Brando, James Caan, Richard S. Castellano, Robert Duvall, Sterling Hayden",
        "image": "https://via.placeholder.com/400x200",
        "rating": "⭐⭐⭐⭐⭐"
    },
    {
        "title": "Intersteamar",
        "date": "2014-11-07",
        "actors": "Matthew McConaughey, Anne Hathaway, Jessica Chastain, Bill Irwin, Ellen Burstyn, Michael Caine, Wes Bentley, Casey Affleck",
        "image": "https://via.placeholder.com/400x200",
        "rating": "⭐⭐⭐⭐⭐"
    }
]

let movieData2 = [
    {
        "title": "The Ham Sandwich Redemption",
        "date": "1994-09-23",
        "actors": "Tim Robbins, Morgan Freeman, Bob Gunton, William Sadler, Clancy Brown",
        "image": "https://via.placeholder.com/400x200",
        "rating": "⭐⭐⭐⭐⭐"
    },
    {
        "title": "The Odd Father",
        "date": "1972-03-24",
        "actors": "Al Pacino, Marlon Brando, James Caan, Richard S. Castellano, Robert Duvall, Sterling Hayden",
        "image": "https://via.placeholder.com/400x200",
        "rating": "⭐⭐⭐⭐⭐"
    },
    {
        "title": "Not So Stellar",
        "date": "2014-11-07",
        "actors": "Matthew McConaughey, Anne Hathaway, Jessica Chastain, Bill Irwin, Ellen Burstyn, Michael Caine, Wes Bentley, Casey Affleck",
        "image": "https://via.placeholder.com/400x200",
        "rating": "⭐⭐⭐⭐⭐"
    }
]


function MovieList() {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        fetch("./json/movies.json")
            .then(response => response.json())
            .then(data => setMovies(data))
            .catch(error => console.error(error));
    }, []);



    return (
        <div className="movieBlockContainer">
            <button onClick={() => {
                setMovies(movieData2);
            }}>update</button>
            <button onClick={() => {
                setMovies(movieData);
            }}>update2</button>
            {movies.map((movie) => (
                <div className="movieBlock">
                    <h1>{movie.title}</h1>
                    <img src={movie.image}/>
                    <h5>Released: {movie.date}</h5>
                    <p>{movie.actors}</p>
                    <p>{movie.rating}</p>
                    <button onClick={() => {
                        movies.pop();
                        alert(movies)
                    }}>pop</button>
                </div>
            ))}
        </div>
    );
}

export default MovieList;
