import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navigation from "./components/Navigation";
import MovieList from "./pages/MovieList";
import AddMovie from "./pages/AddMovie";
import {useEffect, useState} from "react";
function App() {

    let [movies, setMovies] = useState(null);

    function deleteMovie(id){
        setMovies(movies.filter(movie => movie.id !== id))
    }

    function addMovieReview(){

        movies.push(testMovie);
        setMovies(movies.filter(movie => movie.id != null));
    }

    useEffect(() => {
        fetch("./json/movies.json")
            .then(response => response.json())
            .then((data) => {
                console.log(data);
                setMovies(data);
            })
            .catch(error => console.error(error));
    }, []);

    if(movies !== null){
        return (
            <div className="App">
                <Navigation />
                <Router>
                    <Routes>
                        <Route path="/" element={<MovieList movies={movies} deleteMovie={deleteMovie} />} />
                        <Route path="/addMovie" element={<AddMovie movies={movies} addMovieReview={addMovieReview}/>} />
                    </Routes>
                </Router>
                <button onClick={() => {
                        addMovieReview();
                    console.log(movies)
                    }
                }>Add Movie</button>
            </div>
        )
    }
    else{
        return "loading";
    }

}

export default App;
