import React, { useState, useEffect } from 'react';

function AddMovie(props){

    const [addMovie, setAddMovie] = useState({
        "id": null,
        "title": null,
        "date": null,
        "actors": null,
        "image": null,
        "rating": null
    });

    const handleSubmit = (event) => {
        event.preventDefault();
        props.movies.push(addMovie);
    };


    return(
        <div>
            <h2>Add A Movie Rating</h2>
            <form onSubmit={handleSubmit}>
                <label>Title</label>
                <input type="text" id="title" value={title} onChange={(event) => setAddMovie({...addMovie, title: event.target.value})} />

                <label>Date</label>
                <input type="date" id="date" value={date} onChange={(event) => setDate(event.target.value)} />

                <label>Actors</label>
                <input type="text" id="actors" value={actors} onChange={(event) => setActors(event.target.value)} />

                <label>Image</label>
                <input type="url" id="image" value={image} onChange={(event) => setImage(event.target.value)} />

                <label>Rating</label>
                <input type="number" id="rating" value={rating} onChange={(event) => setRating(event.target.value)} />

                <button type="submit">Submit</button>
            </form>
        </div>

    );
}

export default AddMovie;


