import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';


function MovieList(props) {

    let myMovies = props.movies;
    //let delMovie = props.deleteMovie;

    const cardContainer = {
        backgroundColor: 'black',
        fontSize: '16px',
        color: 'white'
    };


    return (
        <div className="movieBlockContainer">
            {myMovies.map((movie) => (
                <div key={movie.id} className={"w-25 p-lg-3 bg-dark"} style={cardContainer}>

                    <Card className={"w-100 p-lg-3 bg-secondary"}>
                        <Card.Img variant="top" src={movie.image} />
                        <Card.Body>
                            <Card.Title className={"fs-2"}>{movie.title}</Card.Title>
                            <Card.Text className={"text-grey"}>
                                {movie.actors}
                            </Card.Text>
                        </Card.Body>
                        <ListGroup className="list-group-flush">
                            <ListGroup.Item>Released: {movie.date}</ListGroup.Item>
                            <ListGroup.Item>{movie.rating}</ListGroup.Item>
                            <ListGroup.Item>
                                <Button variant="outline-secondary" onClick={() => {
                                props.deleteMovie(movie.id);
                                }}>Delete</Button>
                            </ListGroup.Item>
                        </ListGroup>
                    </Card>


                </div>
            ))}
        </div>
    );

}

export default MovieList;
